      setTodos(storage);
    })
  }) */

  return (
    <div className="App">
      {/* children beispiel */}
      <VL1603 onSmash={() => alert("clicked")}>
        Click
      </VL1603>
      <Header title="To-Do App"/>
      <List item={todos} setTodos={setTodos}/>
      <Input todos={todos} setTodos={setTodos}/>
    </div>
  )
}

export default App;
